    this.visible = true;
    this.isDown = false;
    this.lastIsDown = false;

    this.cursorPos = new Float32Array(3);
    this.hoveringObject = null;

    if (WL.xrSession) {
        this.setupVREvents(WL.xrSession);
    }
    WL.onXRSessionStart.push(this.setupVREvents.bind(this));

